import React from 'react';
import '../index.css';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import PopupAdd from './PopupAdd';
import PopupConfirm from './PopupConfirm';
import PopupEdit from './PopupEdit';
import PopupAvatar from './PopupAvatar';
import PopupImage from './PopupImage';

function App() {

  const [isEditProfilePopupOpen, setEditProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setAddPlacePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setEditAvatarPopupOpen] = React.useState(false);
  const [isConfirmPopupOpen, setConfirmPopupOpen] = React.useState(false);
  const [selectedCard, setSelectedCard] = React.useState(null);

  function handleCardClick(card) {
    setSelectedCard(card);
  }

  function handleEditAvatarClick() {
    setEditAvatarPopupOpen(true);
  }

  function handleEditProfileClick() {
    setEditProfilePopupOpen(true);
  }

  function handleAddPlaceClick() {
    setAddPlacePopupOpen(true);
  }

  function handleConfirmClick() {
    setConfirmPopupOpen(true);
  }

  const closeAllPopups = () => {
    setEditProfilePopupOpen(false);
    setAddPlacePopupOpen(false);
    setEditAvatarPopupOpen(false);
    setConfirmPopupOpen(false);
  }

  return (
    <body className="page">

      <Header />

      <Main onAddPlace={handleAddPlaceClick} 
        onEditProfile={handleEditProfileClick} 
        onEditAvatar={handleEditAvatarClick}
        onCardClick={handleCardClick}
        onDeleteClick={handleConfirmClick}/>

      <Footer />

      <PopupAdd isOpen={isAddPlacePopupOpen} onClose={closeAllPopups} />

      <PopupEdit isOpen={isEditProfilePopupOpen} onClose={closeAllPopups} />

      <PopupConfirm isOpen={isConfirmPopupOpen} onClose={closeAllPopups} />

      <PopupAvatar isOpen={isEditAvatarPopupOpen} onClose={closeAllPopups} />

      <PopupImage isOpen={selectedCard} onClose={closeAllPopups} />

      <template id="cardTemplate">
        <article className="element">
          <button className="element__delete-button button"></button>
          <img src="#" alt="Фото" className="element__img" />
          <div className="element__footer">
            <h3 className="element__title"></h3>
            <button className="element__like-button button" type="button" aria-label="Кнопка 'понравилось'"></button>
            <p className="element__like-counter">0</p>
            </div>
        </article>
      </template>
    </body>
  );
}

export default App;
