function Main() {

  const handleEditAvatarClick = () => {
    document.querySelector('#popupUpdate').classList.add('popup_opened');
  }

  const avatarButton = document.querySelector('.profile__avatar-pen');
  avatarButton.addEventListener('click', handleEditAvatarClick);

  return (
    <main className="container">

      <section className="profile">
        <div className="profile__avatar-container">
          <div className="profile__avatar-overlay">
            <div class="profile__avatar-pen"></div>
          </div>
          <img src="#" alt="Аватар профиля" className="profile__avatar" />
        </div>

        <div className="profile__info">
          <div className="profile__head">
            <h1 className="profile__title" name="name"></h1>
            <p className="profile__subtitle" name="about"></p>
          </div>
          <button className="profile__edit-button button" type="button" aria-label="Кнопка редактирования профиля"></button>
        </div>
        <button className="profile__add-button button" type="button" aria-label="Кнопка добавления"></button>
      </section>

      <section className="elements">

      </section>
    </main>
  )
}

export default Main;
