import React from 'react';
import '../index.css';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import PopupAdd from './PopupAdd';
import PopupConfirm from './PopupConfirm';
import PopupEdit from './PopupEdit';
import PopupAvatar from './PopupAvatar';

function App() {

  function handleEditAvatarClick() {
    document.querySelector('.popup_type_avatar').classList.add('popup_opened');
  }

  function handleEditProfileClick() {
    document.querySelector('.popup_type_edit').classList.add('popup_opened');
  }

  function handleAddPlaceClick() {
    document.querySelector('.popup_type_add-card').classList.add('popup_opened');
  }

  return (
    <body className="page">

      <Header />

      <Main />

      <Footer />

      <PopupAdd />

      <PopupEdit />

      <PopupConfirm />

      <PopupAvatar />

      <template id="cardTemplate">
        <article className="element">
          <button className="element__delete-button button"></button>
          <img src="#" alt="Фото" className="element__img" />
          <div className="element__footer">
            <h3 className="element__title"></h3>
            <button className="element__like-button button" type="button" aria-label="Кнопка 'понравилось'"></button>
            <p className="element__like-counter">0</p>
            </div>
        </article>
      </template>
    </body>
  );
}

export default App;
