function PopupImage(card, { isOpen, onClose }) {

  const closePopupOverlay = (evt) => {
    if (evt.target.classList.contains('popup') || evt.target.classList.contains('popup__close-button')) {
      onClose();
    }
  }

  return(
    <div className={"popup popup_action_open-img" + (isOpen ? " popup_opened" : "")} id="popupOpenImg" onClick={closePopupOverlay}>
      <div className="popup__container popup__container_for_image">
        <button className="popup__close-button button" id="closeButtonPopupImage" type="reset" aria-label="Кнопка закрытия всплывающего окна"></button>
        <img src={card.link} alt="Фото" className="popup__img" id="popupImg" />
        <p className="popup__description">{card.name}</p>
      </div>
    </div>
  )
}

export default PopupImage;
